			select 
				t1.CONTENT_ID
			   ,[ATTRIBUTE_NAME]
			   ,[FORMAT_MASK]
			   ,[INPUT_MASK]
			   ,[ATTRIBUTE_SIZE]
			   ,NULL--[DEFAULT_VALUE]
			   ,[ATTRIBUTE_TYPE_ID]
