			   ,[ATTRIBUTE_ORDER]
			   ,0--[REQUIRED]
			   ,[PERMANENT_FLAG]
			   ,0--[PRIMARY_FLAG]
			   ,NULL--[RELATION_CONDITION]
			   ,0--[display_as_radio_button]
			   ,[view_in_list]
			   ,[READONLY_FLAG]
			   ,[allow_stage_edit]
			   ,[ATTRIBUTE_CONFIGURATION]
			   ,NULL--[RELATED_IMAGE_ATTRIBUTE_ID]
			   ,NULL--[persistent_attr_id]
			   ,NULL--[join_attr_id]
			   ,NULL--[link_id]
			   ,NULL--[DEFAULT_BLOB_VALUE]
			   ,[AUTO_LOAD]
			   ,[FRIENDLY_NAME]
			   ,[use_site_library]
			   ,0--[use_archive_articles]
			   ,[AUTO_EXPAND]
			   ,NULL--[relation_page_size]
			   ,[doctype]
			   ,[full_page]
			   ,[MULTILINGUAL]
			   ,[PARENT_CONTENT_ATTRIBUTE_ID]
			   ,[MLANGUAGE_ID] 
			from #temp_insert_content_attribute t1 inner join content t2
				on t1.content_id = t2.content_id
			where t2.virtual_type = 0
			

			insert into content_attribute
			([CONTENT_ID]
			   ,[ATTRIBUTE_NAME]
			   ,[FORMAT_MASK]
			   ,[INPUT_MASK]
			   ,[ATTRIBUTE_SIZE]
			   ,[DEFAULT_VALUE]
			   ,[ATTRIBUTE_TYPE_ID]
			   ,[RELATED_ATTRIBUTE_ID]
			   ,[INDEX_FLAG]
			   ,[DESCRIPTION]
			   ,[MODIFIED]
			   ,[CREATED]
			   ,[LAST_MODIFIED_BY]
			   ,[ATTRIBUTE_ORDER]
			   ,[REQUIRED]
			   ,[PERMANENT_FLAG]
			   ,[PRIMARY_FLAG]
			   ,[RELATION_CONDITION]
			   ,[display_as_radio_button]
			   ,[view_in_list]
			   ,[READONLY_FLAG]
			   ,[allow_stage_edit]
			   ,[ATTRIBUTE_CONFIGURATION]
			   ,[RELATED_IMAGE_ATTRIBUTE_ID]
			   ,[persistent_attr_id]
			   ,[join_attr_id]
			   ,[link_id]
			   ,[DEFAULT_BLOB_VALUE]
			   ,[AUTO_LOAD]
			   ,[FRIENDLY_NAME]
			   ,[use_site_library]
			   ,[use_archive_articles]
			   ,[AUTO_EXPAND]
			   ,[relation_page_size]
			   ,[doctype]
			   ,[full_page]
			   ,[MULTILINGUAL]
			   ,[PARENT_CONTENT_ATTRIBUTE_ID]
			   ,[MLANGUAGE_ID])
			select	ca2.CONTENT_ID
			   ,[ATTRIBUTE_NAME]
			   ,[FORMAT_MASK]
			   ,[INPUT_MASK]
			   ,[ATTRIBUTE_SIZE]
			   ,NULL--[DEFAULT_VALUE]
			   ,[ATTRIBUTE_TYPE_ID]
			   ,NULL--[RELATED_ATTRIBUTE_ID]
			   ,[INDEX_FLAG]
			   ,[DESCRIPTION]
			   ,GETDATE()--[MODIFIED]
			   ,GETDATE()--[CREATED]
			   ,[LAST_MODIFIED_BY]
			   ,[ATTRIBUTE_ORDER]
			   ,0--[REQUIRED]
			   ,[PERMANENT_FLAG]
			   ,0--[PRIMARY_FLAG]
			   ,NULL--[RELATION_CONDITION]
			   ,0--[display_as_radio_button]
			   ,0--[view_in_list]
			   ,[READONLY_FLAG]
			   ,0--[allow_stage_edit]
			   ,[ATTRIBUTE_CONFIGURATION]
			   ,NULL--[RELATED_IMAGE_ATTRIBUTE_ID]
			   ,ca1.ATTRIBUTE_ID--[persistent_attr_id]
			   ,[join_attr_id]
			   ,[link_id]
			   ,NULL--[DEFAULT_BLOB_VALUE]
			   ,[AUTO_LOAD]
			   ,NULL--[FRIENDLY_NAME]
			   ,[use_site_library]
			   ,0--[use_archive_articles]
			   ,[AUTO_EXPAND]
			   ,NULL--[relation_page_size]
			   ,[doctype]
			   ,[full_page]
			   ,[MULTILINGUAL]
			   ,NULL--[PARENT_CONTENT_ATTRIBUTE_ID]
			   ,[MLANGUAGE_ID] 
			from #temp_insert_content_attribute ca1 cross join (select v_content.CONTENT_ID from Content base_content inner join Content v_content 
				on base_content.content_id = v_content.virtual_join_primary_content_id
				where base_content.content_id = @content_id) ca2  
			drop table #temp_insert_content_attribute
			set @j = @j + 1
		end
	end
END
