  inner join deleted d on d.link_id = ii.link_id and (
  (ii.item_id = d.l_item_id and ii.linked_item_id = d.r_item_id)
  or
  (ii.item_id = d.r_item_id and ii.linked_item_id = d.l_item_id)
  )
END
