CREATE PROCEDURE [dbo].[qp_copy_user_group]
	@group_id numeric(18,0),
	@new_name nvarchar(255),
	@create_by_id numeric(18,0),
	@new_group_id numeric(18,0) output
AS
BEGIN
	declare @now datetime;
	set @now = getdate();
	
	INSERT INTO [USER_GROUP]
		   ([GROUP_NAME]
		   ,[DESCRIPTION]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY]
		   ,[shared_content_items]
		   ,[nt_group]
		   ,[ad_sid]
		   ,[BUILT_IN]
		   ,[READONLY]
		   ,[use_parallel_workflow])
	 SELECT @new_name
		  ,[DESCRIPTION]
		  ,@now
		  ,@now
		  ,@create_by_id
		  ,[SHARED_CONTENT_ITEMS]
		  ,[NT_GROUP]
		  ,[AD_SID]
		  ,0
		  ,0
		  ,[USE_PARALLEL_WORKFLOW]
	FROM [USER_GROUP]
	WHERE [GROUP_ID] = @group_id

	set @new_group_id = scope_identity();
	
	
	INSERT INTO [GROUP_TO_GROUP] ([CHILD_GROUP_ID] ,[PARENT_GROUP_ID])
	SELECT @new_group_id, [PARENT_GROUP_ID] from [GROUP_TO_GROUP] WHERE [CHILD_GROUP_ID] = @group_id

	
	INSERT INTO [USER_GROUP_BIND] ([GROUP_ID],[USER_ID])
	select @new_group_id, [USER_ID] from [USER_GROUP_BIND] where [GROUP_ID] = @group_id
	
	INSERT INTO [TAB_ACCESS]([TAB_ID],[USER_ID],[GROUP_ID],[PERMISSION_LEVEL_ID])
	select [TAB_ID],[USER_ID],@new_group_id,[PERMISSION_LEVEL_ID] from [TAB_ACCESS] where [GROUP_ID] = @group_id

	INSERT INTO [SITE_ACCESS]
		   ([SITE_ID]
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY]
		   ,[propagate_to_contents])
	SELECT [SITE_ID]
		  ,[USER_ID]
		  ,@new_group_id
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
		  ,[propagate_to_contents]
	FROM [SITE_ACCESS]
	where [GROUP_ID] = @group_id
	
	INSERT INTO CONTENT_ACCESS
		   ([CONTENT_ID]
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY]
		   ,[propagate_to_items])
	SELECT [CONTENT_ID]
		  ,[USER_ID]
		  ,@new_group_id
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
		  ,[propagate_to_items]
	FROM [CONTENT_ACCESS]
	where [GROUP_ID] = @group_id
	
	INSERT INTO CONTENT_ITEM_ACCESS
		   (CONTENT_ITEM_ID
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY])
	SELECT CONTENT_ITEM_ID
		  ,[USER_ID]
		  ,@new_group_id
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
	FROM CONTENT_ITEM_ACCESS
	where [GROUP_ID] = @group_id
	
	INSERT INTO FOLDER_ACCESS
		   (FOLDER_ID
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY])
	SELECT FOLDER_ID
		  ,[USER_ID]
		  ,@new_group_id
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
	FROM FOLDER_ACCESS
	where [GROUP_ID] = @group_id
	
	INSERT INTO ENTITY_TYPE_ACCESS
		   (ENTITY_TYPE_ID
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY])
	SELECT ENTITY_TYPE_ID
		  ,[USER_ID]
		  ,@new_group_id
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
	FROM ENTITY_TYPE_ACCESS
	where [GROUP_ID] = @group_id
	
	INSERT INTO ACTION_ACCESS
		   (ACTION_ID
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY])
	SELECT ACTION_ID
		  ,[USER_ID]
		  ,@new_group_id
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
	FROM ACTION_ACCESS
	where [GROUP_ID] = @group_id
END
GO

