update ENTITY_TYPE
set ENTITY_TYPE.ACTION_PERMISSION_ENABLE = 1
FROM ENTITY_TYPE
JOIN
(	select distinct T.ID from BACKEND_ACTION A
	join ENTITY_TYPE T ON A.ENTITY_TYPE_ID = T.ID
) T ON T.ID = ENTITY_TYPE.ID
