exec qp_drop_existing 'qp_copy_user', 'IsProcedure'
GO
CREATE PROCEDURE [dbo].[qp_copy_user]
	@user_id numeric(18,0),
	@new_login nvarchar(30),
	@create_by_id numeric(18,0),
	@new_user_id numeric(18,0) output
AS
BEGIN
	declare @now datetime;
	set @now = getdate();
	
	INSERT INTO [USERS]
		([PASSWORD]
		,[DISABLED]
		,[FIRST_NAME]
		,[LAST_NAME]
		,[EMAIL]
		,[AUTO_LOGIN]
		,[NT_LOGIN]
		,[LAST_LOGIN]
		,[SUBSCRIBED]
		,[CREATED]
		,[MODIFIED]
		,[LAST_MODIFIED_BY]
		,[LANGUAGE_ID]
		,[VMODE]
		,[ad_sid]
		,[allow_stage_edit_field]
		,[allow_stage_edit_object]
		,[BUILT_IN]
		,[LOGIN]
		,[hash]
		,[salt]
		,[PASSWORD_MODIFIED])
	SELECT ''
		  ,[DISABLED]
		  ,[FIRST_NAME]
		  ,[LAST_NAME]
		  ,[EMAIL]
		  ,[AUTO_LOGIN]
		  ,[NT_LOGIN]
		  ,[LAST_LOGIN]
		  ,[SUBSCRIBED]
		  ,@now
		  ,@now
		  ,@create_by_id
		  ,[LANGUAGE_ID]
		  ,[VMODE]
		  ,[ad_sid]
		  ,[allow_stage_edit_field]
		  ,[allow_stage_edit_object]
		  ,0
		  ,@new_login
		  ,[hash]
		  ,[salt]
		  ,[PASSWORD_MODIFIED]
	FROM [USERS]
	WHERE [USER_ID] = @user_id

	set @new_user_id = scope_identity();

	delete from [USER_GROUP_BIND] where [USER_ID] = @new_user_id
	INSERT INTO [USER_GROUP_BIND] ([GROUP_ID],[USER_ID])
	select [GROUP_ID], @new_user_id from [USER_GROUP_BIND] where [USER_ID] = @user_id
		
	INSERT INTO [TAB_ACCESS]([TAB_ID],[USER_ID],[GROUP_ID],[PERMISSION_LEVEL_ID])
	select [TAB_ID],@new_user_id,[GROUP_ID],[PERMISSION_LEVEL_ID] from [TAB_ACCESS] where [USER_ID] = @user_id

	INSERT INTO [SITE_ACCESS]
		   ([SITE_ID]
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY]
		   ,[propagate_to_contents])
	SELECT [SITE_ID]
		  ,@new_user_id
		  ,[GROUP_ID]
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
		  ,[propagate_to_contents]
	FROM [SITE_ACCESS]
	where [USER_ID] = @user_id

	INSERT INTO CONTENT_ACCESS
		   ([CONTENT_ID]
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY]
		   ,[propagate_to_items])
	SELECT [CONTENT_ID]
		  ,@new_user_id
		  ,[GROUP_ID]
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
		  ,[propagate_to_items]
	FROM [CONTENT_ACCESS]
	where [USER_ID] = @user_id

	INSERT INTO CONTENT_ITEM_ACCESS
		   (CONTENT_ITEM_ID
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY])
	SELECT CONTENT_ITEM_ID
		  ,@new_user_id
		  ,[GROUP_ID]
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
	FROM CONTENT_ITEM_ACCESS
	where [USER_ID] = @user_id

	INSERT INTO FOLDER_ACCESS
		   (FOLDER_ID
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY])
	SELECT FOLDER_ID
		  ,@new_user_id
		  ,[GROUP_ID]
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
	FROM FOLDER_ACCESS
	where [USER_ID] = @user_id
	
	INSERT INTO ENTITY_TYPE_ACCESS
		   (ENTITY_TYPE_ID
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY])
	SELECT ENTITY_TYPE_ID
		  ,@new_user_id
		  ,[GROUP_ID]
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
	FROM ENTITY_TYPE_ACCESS
	where [USER_ID] = @user_id
	
	INSERT INTO ACTION_ACCESS
		   (ACTION_ID
		   ,[USER_ID]
		   ,[GROUP_ID]
		   ,[PERMISSION_LEVEL_ID]
		   ,[CREATED]
		   ,[MODIFIED]
		   ,[LAST_MODIFIED_BY])
	SELECT ACTION_ID
		  ,@new_user_id
		  ,[GROUP_ID]
		  ,[PERMISSION_LEVEL_ID]
		  ,@now
		  ,@now
		  ,@create_by_id
	FROM ACTION_ACCESS
	where [USER_ID] = @user_id
	
	insert into USER_DEFAULT_FILTER ([USER_ID], CONTENT_ID, CONTENT_ITEM_ID)
	select @new_user_id, CONTENT_ID, CONTENT_ITEM_ID from USER_DEFAULT_FILTER where [USER_ID] = @user_id
END
GO

